import React, { useEffect, useState } from 'react';
import './Home.css'; // Import CSS tùy chỉnh cho trang Home
import { Link } from 'react-router-dom';
import axios from '../api/api';

const Home = () => {
  const [articles, setArticles] = useState([]);
  const [visibleArticles, setVisibleArticles] = useState([]);
  const [startIndex, setStartIndex] = useState(0);

  useEffect(() => {
    // Gọi API để lấy dữ liệu bài viết
    axios.get('/categories/671a6da492f44555b0b5f943/articles')
      .then(response => {
        const { articles } = response.data;
        setArticles(articles);
        setVisibleArticles(articles.slice(0, 4));
      })
      .catch(error => console.error('Error fetching articles:', error));
  }, []);

  useEffect(() => {
    if (articles.length > 4) {
      // Thay đổi các bài viết được hiển thị mỗi 5 giây nếu có hơn 4 bài
      const interval = setInterval(() => {
        const nextIndex = (startIndex + 4) % articles.length;
        const newVisibleArticles = articles.slice(nextIndex, nextIndex + 4);

        // Nếu không đủ 4 bài còn lại, quay lại từ đầu để đảm bảo luôn có 4 bài
        if (newVisibleArticles.length < 4) {
          const additionalArticles = articles.slice(0, 4 - newVisibleArticles.length);
          setVisibleArticles([...newVisibleArticles, ...additionalArticles]);
        } else {
          setVisibleArticles(newVisibleArticles);
        }

        setStartIndex(nextIndex);
      }, 5000);

      return () => clearInterval(interval); // Clear interval khi component unmount
    } else {
      // Nếu có 4 bài trở xuống, vẫn nháy mỗi 5 giây nhưng không thay đổi nội dung
      const interval = setInterval(() => {
        setVisibleArticles(articles);
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [articles, startIndex]);

  return (
    <div className="home-container">
      {/* Hàng đầu tiên với hai cột */}
      <div className="first-row">
        <div className="column-left">
          <h2>CÔNG TY CỔ PHẦN TẬP ĐOÀN THƯƠNG MẠI BẮC HÀ</h2>
          <div className="buttons-row">
            <button className="btn btn-about">Về BAK Group</button>
            <button className="btn btn-register">Đăng ký sử dụng dịch vụ</button>
          </div>
        </div>
        <div className="column-right">
          <img
            src="/images/bak1.jpg" // Thay bằng đường dẫn tới ảnh đại diện của bạn
            alt="BachaGroup"
            className="representative-image"
          />
        </div>
      </div>

      {/* Hàng thứ hai với hai cột */}
      <div className="second-row">
        <div className="second-column column-left">
          <img
            src="/images/bak2.jpg" // Thay bằng đường dẫn tới ảnh cho sứ mệnh
            alt="Mission Image"
            className="mission-image"
          />
        </div>
        <div className="second-column column-right">
          <h3>Sứ mệnh của BAK</h3>
          <p>Vì một Việt Nam Thịnh Vượng</p>
          <ul className="mission-list">
            <li>Tổ chức sự kiện</li>
            <li>Tổ chức hội chợ, triển lãm</li>
            <li>Truyền thông quảng cáo</li>
          </ul>
          <button className="btn btn-services">Tìm hiểu các dịch vụ</button>
        </div>
      </div>

      {/* Phần hiển thị các dịch vụ của Bắc Hà */}
      <div className="services-container">
        <h2>Các dịch vụ của Bắc Hà</h2>
        <div className="articles-grid">
          {visibleArticles.map(article => (
            <div className="article-card" key={article._id}>
              <h3 className="article-title">{article.title}</h3>
              <img
                src={`http://localhost:5004/${article.thumbnailImage}`}
                alt={article.title}
                className="article-image"
              />
              <p className="article-summary">{article.summary}</p>
              <Link to={`/article-detail/${article.key}`} className="article-link">Chi tiết</Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
